getByBSSID.map:
function(doc) {
           	if("bssid" in doc) {
           		emit(doc.bssid, doc);
           	}
           }

getByCellID.map:
function(doc) {
           	if("cellId" in doc) {
           		emit(doc.cellId, doc);
           	}
           }

getByMACAddress.map:
function(doc) {
           	if("MAC" in doc) {
           		emit(doc.MAC, doc);
           	}
           }
